definition.script:
pipeline {
    agent any
    stages {
        stage('Preparation') {
            steps {
                git 'https://bitbucket.org/Adaptavist/tm4j-cucumber-integration-example.git'
            }
        }
        stage('Clean Work Space'){
            steps {
                sh 'mvn clean'
            }
        }
        stage('Build') {
            steps {
                sh 'mvn test'
            }
        }
    }
    post {
        always {
            tm4jPostBuild serverAddress: 'http://localhost:2990/jira', 
                    projectKey: 'JQA', filePath:'target/cucumber/*.json', 
                    format: 'Cucumber', 
                    autoCreateTestCases: false
        }
    }
}
